import React from 'react'
import { useState, useEffect } from 'react';


function ClickLove() {
    const [showPassword, setShowPassword] = useState(false);

    return (
        <div>
            <span type={showPassword ? "text" : "password"}>
            <i className={`bi-heart${showPassword ? "-fill" : ""}`}
                onClick={() => setShowPassword(!showPassword)}
                id="icon-showMdp" style={{color: "red"}}
            ></i>
            <iframe src="https://animationdigitalnetwork.fr/embedded/one-piece-oav-episode-de-luffy/19389" frameborder="0" width="640px" height="360px" allowfullscreen></iframe><br/><a href="https://animationdigitalnetwork.fr/video/one-piece-oav-episode-de-luffy/19389-oav-aventure-sur-l-ile-de-la-main">Source : One Piece OAV • Épisode de Luffy - OAV en VOSTFR et VF sur ADN</a>
            </span>
        </div>
    )
}

export default ClickLove
