import React from 'react'
import portfolio from '../tools/image/portfolio darkmode.png'
import { useState } from 'react';

function Body_Projets() {
    const itemsPerPage = 3; // Number of items to display per page
    const [currentPage, setCurrentPage] = useState(1);

    const projects = [
        { title: 'Mon portfolio', tags: '#Reactjs, #Bootstrap, #CSS' },
        { title: 'COSEF-INFOS', tags: '#Reactjs, #CSS, #ExpressJs' },
        { title: 'TRAVEL CALCULATOR', tags: '#HTML, #CSS, #Javascript' },
        { title: 'TRAVEL CALCULATOR', tags: '#HTML, #CSS, #Javascript' },
        { title: 'TRAVEL CALCULATOR', tags: '#HTML, #CSS, #Javascript' },
        { title: 'TRAVEL CALCULATOR', tags: '#HTML, #CSS, #Javascript' },
        { title: 'TRAVEL CALCULATOR', tags: '#HTML, #CSS, #Javascript' },
        { title: 'TRAVEL CALCULATOR', tags: '#HTML, #CSS, #Javascript' },
        { title: 'TRAVEL CALCULATOR', tags: '#HTML, #CSS, #Javascript' },
        { title: 'TRAVEL CALCULATOR', tags: '#HTML, #CSS, #Javascript' },
        { title: 'TRAVEL CALCULATOR', tags: '#HTML, #CSS, #Javascript' },
        { title: 'TRAVEL CALCULATOR', tags: '#HTML, #CSS, #Javascript' },
        // Add more projects as needed
    ];

    // Calculate the index range for the current page
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;

    // Get the projects to display on the current page
    const projectsToShow = projects.slice(startIndex, endIndex);

    const totalPages = Math.ceil(projects.length / itemsPerPage);

    const handlePageChange = (page) => {
        setCurrentPage(page);
    };
    return (
        <div style={{ paddingTop: "5em" }} className='ps-3 pe-3'>
            <main>
                <div className='bg-white p-4 pe-5 ps-5 pb-4 rounded-1 d-flex flex-column align-items-center'>
                    <section>
                        <p className='text-primary fs-3 fw-bold'>Mes Projets</p>
                    </section>
                    <section className='d-flex flex-row justify-content-between gap-4'>
                        {projectsToShow.map((project, index) => (
                            <article
                                key={index}
                                className='bg-light p-2 rounded-2 ms-2 projetArticle'
                                style={{ border: "2px solid rgba(0, 0, 0, 0.075)" }}
                            >
                                {/* img */}
                                <picture>
                                    <img src={portfolio} alt="portfolio" style={{ width: "24em", height: "15em", objectFit: "cover" }} />
                                </picture>
                                {/* desc */}
                                <picture>
                                    <p className='fw-bold fs-5 p-20'>{project.title}</p>
                                    <p>{project.tags}</p>
                                </picture>
                            </article>
                        ))}
                    </section>
                    <span className='d-flex flex-row me-1'>
                        {Array.from({ length: totalPages }, (_, i) => (

                            <i className='bi-dot fs-3'
                                key={i + 1}
                                onClick={() => handlePageChange(i + 1)}
                            ></i>

                        ))}
                    </span>
                    {/* Pagination */}
                    <div className='pagination'>
                        <button onClick={() => setCurrentPage(currentPage - 1)} disabled={currentPage === 1}>
                            <i className='bi-chevron-left'></i>
                        </button>
                        {Array.from({ length: totalPages }, (_, i) => (
                            <button
                                key={i + 1}
                                onClick={() => handlePageChange(i + 1)}
                                className={currentPage === i + 1 ? 'active-page' : ''}
                            >
                                {i + 1}
                            </button>
                        ))}
                        <button onClick={() => setCurrentPage(currentPage + 1)} disabled={endIndex >= projects.length}>
                            <i className='bi-chevron-right'></i>
                        </button>
                    </div>
                </div>
            </main >
        </div >
    )
}

export default Body_Projets
